//-----------------------Dropdownrightgroup-------------------------------------
function Sub1(SYS_Name){
 if(document.getElementById(SYS_Name).style.display=="none"){ 
document.getElementById(SYS_Name).style.display="block";
}else{ 
document.getElementById(SYS_Name).style.display="none";
}
}
